'use strict';

var promisify = require('promisify-es6');

var ConcatStream = require('concat-stream');

var once = require('once');

var isStream = require('is-stream');

var OtherBuffer = require('buffer').Buffer;

var isSource = require('is-pull-stream').isSource;

var FileResultStreamConverter = require('../utils/file-result-stream-converter');

var SendFilesStream = require('../utils/send-files-stream');

module.exports = function (send) {
  var createAddStream = SendFilesStream(send, 'add');
  var add = promisify(function (_files, options, _callback) {
    if (typeof options === 'function') {
      _callback = options;
      options = null;
    }

    var callback = once(_callback);

    if (!options) {
      options = {};
    }

    options.converter = FileResultStreamConverter;
    var ok = Buffer.isBuffer(_files) || isStream.readable(_files) || Array.isArray(_files) || OtherBuffer.isBuffer(_files) || typeof _files === 'object' || isSource(_files);

    if (!ok) {
      return callback(new Error('first arg must be a buffer, readable stream, pull stream, an object or array of objects'));
    }

    var files = [].concat(_files);
    var stream = createAddStream({
      qs: options
    });
    var concat = ConcatStream(function (result) {
      return callback(null, result);
    });
    stream.once('error', callback);
    stream.pipe(concat);
    files.forEach(function (file) {
      return stream.write(file);
    });
    stream.end();
  });
  return function () {
    var args = Array.from(arguments); // If we files.add(<pull stream>), then promisify thinks the pull stream is
    // a callback! Add an empty options object in this case so that a promise
    // is returned.

    if (args.length === 1 && isSource(args[0])) {
      args.push({});
    }

    return add.apply(null, args);
  };
};
